CREATE TABLE IF NOT EXISTS roles (id_role INT AUTO_INCREMENT PRIMARY KEY, rol_name varchar(20) NOT NULL);
CREATE TABLE IF NOT EXISTS exchange_rate (id_exchange INT AUTO_INCREMENT PRIMARY KEY, exchange_rate_currency varchar(20) NOT NULL, calculation_value decimal(18,10) NOT NULL, is_active bit);
CREATE TABLE IF NOT EXISTS users (id_user INT AUTO_INCREMENT PRIMARY KEY,user_name varchar(15) not null, password varchar(90) NOT NULL,id_role int NOT NULL,FOREIGN KEY (id_role) REFERENCES roles(id_role));